import React, { useState } from 'react'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, ...rest } = props

  return didError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={'https://www.google.com/url?sa=i&url=https%3A%2F%2Fru.hostings.info%2Fschools%2F404-not-found.html&psig=AOvVaw38Ipj3Cz09cTlyoYbJO6xt&ust=1757571315104000&source=images&cd=vfe&opi=89978449&ved=0CBUQjRxqFwoTCLjR58DFzY8DFQAAAAAdAAAAABAW'} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={src} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}
